var list_config,titles,currentConfig,layouts=["hero","background","profile","page","card"],currentLayout=0;function refreshGithubCards(){Object.keys(window).forEach(e=>{e.startsWith("updateGithubCard_")&&window[e]()})}function switchHomeLayout(){var e,t,n=currentLayout;currentLayout=currentLayout==layouts.length-1?0:currentLayout+1,e=document.getElementById(layouts[n]),t=document.getElementById(layouts[currentLayout]);const s=document.querySelectorAll("code[id=layout]");t.style.display="block",e.style.display="none",s.forEach(function(e){e.innerText=layouts[currentLayout]}),setTimeout(refreshGithubCards,100)}window.addEventListener("DOMContentLoaded",e=>{document.querySelectorAll("#switch-layout-button").forEach(e=>e.addEventListener("click",function(e){e.preventDefault(),switchHomeLayout()}))}),list_config=["CardViewProse","CardViewScreenWidth","NormalView"],titles={CardViewProse:"card view with constrained width",CardViewScreenWidth:"card view with full width",NormalView:"standard list view"},currentConfig=0;function switchList(){var e,t,n=currentConfig;currentConfig=currentConfig==list_config.length-1?0:currentConfig+1,e=document.getElementById(list_config[n]),t=document.getElementById(list_config[currentConfig]);const s=document.querySelectorAll("code[id=config]");t.style.display="block",e.style.display="none",s.forEach(function(e){e.innerText=titles[list_config[currentConfig]]})}window.addEventListener("DOMContentLoaded",e=>{document.querySelectorAll("#switch-config-button").forEach(e=>e.addEventListener("click",function(e){e.preventDefault(),switchList()}))})